<!DOCTYPE html PUBLIC "-//W3C//DTD XHTML 1.0 Transitional//EN" "http://www.w3.org/TR/xhtml1/DTD/xhtml1-transitional.dtd">
<html xmlns="http://www.w3.org/1999/xhtml">
<head>
<meta http-equiv="Content-Type" content="text/html; charset=utf-8" />
<title>邮箱-找回密码</title>
<link rel="stylesheet" type="text/css" href="/public/admin/css/public.css" />
<link rel="stylesheet" type="text/css" href="/public/admin/css/page.css" />
<script type="text/javascript" src="/public/adminjs/jquery.min.js"></script>
<script type="text/javascript" src="/public/adminjs/public.js"></script>
</head>
<body>

	<!-- 登录页面头部 -->
	<div class="logHead">
		<img src="/public/admin/img/logLOGO1.png" />
	</div>
	<!-- 登录页面头部结束 -->

	<!-- 登录body -->
	<div class="logDiv">
		<img class="logBanner" src="/public/admin/img/logBanner" />
		<form action='/index.php?m=User&a=kemail' method='post'> 
		<div class="logGet">
			<!-- 头部提示信息 -->
			<div class="logD logDtip">
				<p class="p1">邮箱</p>
				<p class="p2">找回密码</p>
			</div>
			<!-- 输入框 -->
			<div class="lgD">
				<img class="img1" src="/public/admin/img/logName.png" /><input type="text"
					name='username' placeholder="输入用户名" />
			</div>
			<div class="lgD">
				<img  class="img1" src="img/logPwd.png" /><input type= 'email'
					placeholder="输入邮箱"  name='email'/>
			</div>
			<div class="logC">
				<button><input type='submit' name='tj' value='找回密码' style="background:#f90;height:43px;width:319px;"></button>
				
			</div>
		</div>
	</div>
	</form>
	<!-- 登录body  end -->

	<!-- 登录页面底部 -->
	
	<!-- 登录页面底部end -->
</body>
</html>